import React, { useRef, useEffect } from 'react';
import './LandingPage.css';
import manImage from './manImage.jpeg';
import footerLogo from './footerLogo.png';

export const LandingPage = () => {
    const manPicRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        manPicRef.current.classList.add('focused');
                    } else {
                        manPicRef.current.classList.remove('focused');
                    }
                });
            },
            { rootMargin: '-50% 0% -50% 0%' } // Adjust this margin as needed
        );

        observer.observe(manPicRef.current);

        return () => {
            observer.unobserve(manPicRef.current);
        };
    }, []);

    return (
        <div>
            <div className="mainDivLandingPage row">
                <div className="div1 col-md-8 col-sm-12">
                    {/* Rest of your content */}
                </div>
                <div className="div2 col-md-3 col-sm-12">
                    <img id='LandingPageManPic' ref={manPicRef} src={manImage} alt="" />
                </div>
            </div>
            <div className="svgImage">
                {/* Your SVG */}
            </div>
        </div>
    );
};
