import React, { useState } from 'react';
import NavBar from '../Navbar';

const CodeEditor = () => {
  const [code, setCode] = useState("console.log('Hello, World!')");

  const runCode = () => {
    const outputFrame = document.getElementById('output');
    const outputDocument = outputFrame.contentDocument || outputFrame.contentWindow.document;
  
    outputDocument.body.innerHTML = '';
  
    // Override console.log to capture the output
    const logs = [];
    const originalConsoleLog = console.log;
    console.log = (...args) => {
      logs.push(args.map(arg => JSON.stringify(arg)).join(' '));
      originalConsoleLog(...args);
    };
  
    try {
      // Execute the code
      eval(code);
  
      // Display captured logs
      logs.forEach(log => {
        const pre = outputDocument.createElement('pre');
        pre.appendChild(outputDocument.createTextNode(log));
        outputDocument.body.appendChild(pre);
      });
    } catch (error) {
      // Display errors
      const pre = outputDocument.createElement('pre');
      pre.style.color = 'red';
      pre.appendChild(outputDocument.createTextNode(error.toString()));
      outputDocument.body.appendChild(pre);
    } finally {
      // Restore console.log to its original state
      console.log = originalConsoleLog;
    }
  };
  

  return (
    <div>
      <NavBar />
      <div style={{ display: 'flex', height: '100vh' }}>
        <div style={{ flex: 1, border: '1px solid #ccc' }}>
          <textarea
            style={{ width: '90', height: '80%', boxSizing: 'border-box' }}
            value={code}
            onChange={(e) => setCode(e.target.value)}
          ></textarea>
          <button onClick={runCode} style={{ width: '100%', height: '20%' }}>
            Run Code
          </button>
        </div>
        <div style={{ flex: 1, border: '1px solid #ccc' }}>
          <iframe title="output" id="output" style={{ width: '100%', height: '100%', border: 0 }}></iframe>
        </div>
      </div>
    </div>
  );
};

export default CodeEditor;
