import React, { useState } from 'react';
import './Template.css';

const TempLate = (prop) => {
    // Initialize an array of objects to track copyStatus for each section
    const [copyStatusArray, setCopyStatusArray] = useState(Array(prop.props.length).fill({ status: 'Copy' }));

    const copyText = (divIndex, subkey) => {
        // Clone the copyStatusArray to avoid mutating the state directly
        const newCopyStatusArray = [...copyStatusArray];

        if (newCopyStatusArray[divIndex].status === 'Copy') {
            newCopyStatusArray[divIndex].status = 'Code Copied';
            const codeTextId = `codeText_${divIndex}_${subkey}`; // Unique ID for this element
            const cText = document.getElementById(codeTextId);

            const tempInput = document.createElement('input');
            tempInput.value = cText.textContent;

            document.body.appendChild(tempInput);
            tempInput.select();
            document.execCommand('copy');
            document.body.removeChild(tempInput);
        } else {
            newCopyStatusArray[divIndex].status = 'Copy';
        }

        // Update the state for the specific section
        setCopyStatusArray(newCopyStatusArray);
    };

    return (
        <>
            <div className="btnDiv">
                <button id="moveToTopBtn" onClick={() => { window.scrollTo({ top: 0, behavior: 'smooth' }) }}>
                    <p>^</p>
                </button>
            </div>
            {
                prop.props.map((item, divIndex) => (
                    <div className="DataSection" key={divIndex}>
                        <div className="mainDivLanding row">
                            <div className="col-12">
                                <p id={`div1Text_${divIndex}`}>{item.topic_name}</p>
                                <h4 style={{ marginLeft: "6%" }}>{item.topic_description}</h4>
                            </div>
                            <p style={{ fontFamily: 'Josefin Sans', marginTop: "3%", marginLeft: "7%" }}>
                                {
                                    item.subtopics.map((item2, subkey) => (
                                        <div key={subkey} className="subtopic-container">
                                            <h4 style={{ marginLeft: "0%", fontFamily: "'Josefin Sans', sans-serif", fontWeight: "700" }}>{item2.subtopic_name}</h4>
                                            <div style={{ backgroundColor: "whitesmoke", width: "90vw" }}>
                                                <h5 style={{ color: "rgb(110, 110, 110)", padding: "1%", marginLeft: "2%", fontFamily: "sans-serif" }}>
                                                    <div className="codeDiv">
                                                        <button className="copyTextBtn" onClick={() => { copyText(divIndex, subkey) }}>
                                                            {copyStatusArray[divIndex].status}
                                                        </button>
                                                        <pre id={`codeText_${divIndex}_${subkey}`} className="codeText">
                                                            {item2.example_code}
                                                        </pre>
                                                    </div>
                                                </h5>
                                            </div>
                                        </div>
                                    ))
                                }
                            </p>
                        </div>
                    </div>
                ))
            }
            {window.scrollTo({ top: 0, behavior: 'smooth' })}
        </>
    );
};

export default TempLate;
