import React, { useRef } from 'react';
import './Adv.css';
import a from './a.png';
import b from './b.png';
import c from './c.png';
import d from './d.png';
import e from './e.png';
import f from './f.png';
import g from './g.png';
import h from './h.png';

const Adv = () => {
    const advRef = useRef(null);
    
    const moveToRight = () => {
        if (advRef.current) {
            // Set the transition to 'none' before changing scrollLeft
            advRef.current.style.transition = 'none';
            advRef.current.scrollLeft += 190;
            
            // Restore the transition after a short delay
            setTimeout(() => {
                advRef.current.style.transition = 'scroll-left 0.5s ease-in-out';
            }, 0); // Delay of 0 milliseconds
        }
    };

    return (
        <div className='AdvParentDiv'>
            <div className="startBtn"><button onClick={moveToRight}>&lt;</button></div>
            <div id='advDiv' ref={advRef}>
                {/* Your images go here */}
            </div>
            <div className="endBtn"></div>
        </div>
    );
}

export default Adv;
