import React, { useRef } from 'react'
import './Adv.css'
import a from './a.png'
import b from './b.png'
import c from './c.png'
import d from './d.png'
import e from './e.png'
import f from './f.png'
import g from './g.png'
import h from './h.png'

const Adv = () => {
    const advRef = useRef(null);
    const moveToRight = () => {
        // if(advRef.current){
            advRef.current.style.transition = 'none';
        advRef.current.scrollLeft += 190;
        setTimeout(() => {
            advRef.current.style.transition = 'scroll-left 0.4 ease-in'
        },900)
        // }
    };
    return (
        <div className='AdvParentDiv'>
            <div className="startBtn"><button onClick={moveToRight}>&lt;</button></div>
            <div id='advDiv' ref={advRef}>
                <img className='advImg' src={b} alt="" />
                <img className='advImg' src={a} alt="" />
                <img className='advImg' src={c} alt="" />
                <img className='advImg' src={d} alt="" />
                <img className='advImg' src={e} alt="" />
                <img className='advImg' src={f} alt="" />
                <img className='advImg' src={g} alt="" />
                <img className='advImg' src={h} alt="" />
                <img className='advImg' src={b} alt="" />
                <img className='advImg' src={a} alt="" />
                <img className='advImg' src={c} alt="" />
                <img className='advImg' src={d} alt="" />
                <img className='advImg' src={e} alt="" />
                <img className='advImg' src={f} alt="" />
                <img className='advImg' src={g} alt="" />
                <img className='advImg' src={h} alt="" />

            </div>
            <div className="endBtn"></div>
        </div>
    )
}

export default Adv
