import React, { useRef } from 'react';
import './Adv.css';
import a from './a.png';
import b from './b.png';
import c from './c.png';
import d from './d.png';
import e from './e.png';
import f from './f.png';
import g from './g.png';
import h from './h.png';

const Adv = () => {
  const advDivRef = useRef(null);

  const moveToRight = () => {
    if (advDivRef.current) {
      // Disable the transition for an immediate scroll
      advDivRef.current.style.transition = 'none';

      // Scroll to the right
      advDivRef.current.scrollLeft += 190;

      // Re-enable the transition for smooth scrolling
      advDivRef.current.style.transition = 'scrollLeft 0.4s ease-in-out';
    }
  };

  return (
    <div className='AdvParentDiv'>
      <div className="startBtn">
        <button onClick={moveToRight}>&lt;</button>
      </div>
      <div id='advDiv' ref={advDivRef} className='advDiv'>
      <img className='advImg' src={b} alt="" />
                <img className='advImg' src={a} alt="" />
                <img className='advImg' src={c} alt="" />
                <img className='advImg' src={d} alt="" />
                <img className='advImg' src={e} alt="" />
                <img className='advImg' src={f} alt="" />
                <img className='advImg' src={g} alt="" />
                <img className='advImg' src={h} alt="" />
                <img className='advImg' src={b} alt="" />
                <img className='advImg' src={a} alt="" />
                <img className='advImg' src={c} alt="" />
                <img className='advImg' src={d} alt="" />
                <img className='advImg' src={e} alt="" />
                <img className='advImg' src={f} alt="" />
                <img className='advImg' src={g} alt="" />
                <img className='advImg' src={h} alt="" />

      </div>
      <div className="endBtn"></div>
    </div>
  );
}

export default Adv;
