import React, { useState, useEffect } from 'react';

const TempLate = (props) => {
    const [showScrollButton, setShowScrollButton] = useState(false);

    // Function to scroll to the top of the page
    const scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth"
        });
    }

    // Show/hide the scroll-to-top button based on scroll position
    const handleScroll = () => {
        if (window.scrollY > 100) {
            setShowScrollButton(true);
        } else {
            setShowScrollButton(false);
        }
    }
    
    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        
        // Clean up the event listener on component unmount
        return () => {
            window.removeEventListener("scroll", handleScroll);
        }
    }, []);
    
    {showScrollButton && (
        <button onClick={scrollToTop} id='scrollToTopButton' title='Go to top'>Top</button>
    )}
    return (
        <>
            {props.props.map((item, key) => (
                <div className='mainDivLanding row' key={key}>
                    <div className="col-12">
                        <p id='div1Text'>{item.topic_name}</p>
                        <h4 style={{ marginLeft: "6%" }}>{item.topic_description}</h4>
                    </div>

                    <p style={{ fontFamily: 'Josefin Sans', padding: "6%", paddingTop: "1%" }}>
                        {item.subtopics.map((item2, key2) => (
                            <div key={key2}>
                                <h4 style={{ marginLeft: "3%" }}>{item2.subtopic_name}</h4>
                                <div style={{ backgroundColor: "whitesmoke", width: "90vw" }}>
                                    <h5 style={{ color: "rgb(110, 110, 110)", padding: "1%", marginLeft: "3%", fontFamily: "sans-serif" }}>
                                        <pre>{item2.example_code}</pre>
                                    </h5>
                                </div>
                            </div>
                        ))}
                    </p>
                </div>
            ))}
            
            {/* Scroll to top button */}
        </>
    );
}

export default TempLate;
