import React, { useState } from 'react';
import './Template.css';

const TempLate = (prop) => {
    // Create an array of state variables, one for each button
    const [copyStatus, setCopyStatus] = useState(Array(prop.props.length).fill('Copy'));

    const copyText = (index) => {
        // Update the specific copyStatus for the clicked button
        const newCopyStatus = [...copyStatus];
        newCopyStatus[index] = 'Code Copied';
        setCopyStatus(newCopyStatus);
    };

    return (
        <>
            <div className="btnDiv">
                <button id='moveToTopBtn' onClick={() => { window.scrollTo(0, 0) }}>^</button>
            </div>
            {prop.props.map((item, key) => (
                <div className='mainDivLanding row' key={key}>
                    <div className="col-12">
                        <p id='div1Text'>{item.topic_name}</p>
                        <h4 style={{ marginLeft: "6%" }}>{item.topic_description}</h4>
                    </div>
                    <p style={{ fontFamily: 'Josefin Sans', padding: "6%", paddingTop: "1%" }}>
                        {item.subtopics.map((item2, subkey) => (
                            <div key={subkey} style={{ backgroundColor: "whitesmoke", width: "90vw" }}>
                                <h4 style={{ marginLeft: "3%" }}>{item2.subtopic_name}</h4>
                                <h5 style={{ color: "rgb(110, 110, 110)", padding: "1%", marginLeft: "3%", fontFamily: "sans-serief" }}>
                                    <div className="copyTextDiv">
                                        <button id={`copyTextBtn${key}-${subkey}`} onClick={() => copyText(subkey)}>
                                            {copyStatus[subkey]}
                                        </button>
                                    </div>
                                    <pre>{item2.example_code}</pre>
                                </h5>
                            </div>
                        ))}
                    </p>
                </div>
            ))}
        </>
    );
};

export default TempLate;
